// 1. Get the news data array from the 'Creating News Digest' node.
const newsItems = $('Creating News Digest').first().json.output;

// 2. SERVER-SIDE RENDERING: Generate the static HTML for the table rows.
let tableRowsHtml = '';

if (!newsItems || newsItems.length === 0) {
    tableRowsHtml = '<tr><td class="pt-8 pb-4 px-8 text-center text-gray-500 dark:text-gray-400">No news items to display.</td></tr>';
} else {
    tableRowsHtml = newsItems.map(item => {
        // Prepare news content for splitting: remove escaped newlines and backslashes
        let newsContent = (item.news_content || "")
            .replace(/\\n/g, " ")
            // *** THIS IS THE CORRECTED LINE ***
            .replace(/\\/g, ""); // Correct regex to find and remove a single backslash

        // Split the content into sentences, keeping the punctuation
        const sentences = newsContent.split(/([.?!])\s+/);

        // Loop through and wrap each sentence in a <p> tag
        let paragraphHtml = '';
        let currentSentence = '';

        const spacingClass = "mb-2"; 

        for (let i = 0; i < sentences.length; i++) {
            let part = sentences[i].trim();

            if (part) {
                if (part.match(/[.?!]/)) {
                    paragraphHtml += `<p class="${spacingClass} dark:text-gray-300">${currentSentence}${part}</p>`;
                    currentSentence = '';
                } else {
                    currentSentence = part;
                }
            }
        }

        if (currentSentence) {
            paragraphHtml += `<p class="${spacingClass} dark:text-gray-300">${currentSentence}</p>`;
        }

        const sourcesHtml = (item.news_sources || [])
            .map(
              (sourceUrl, i) =>
                `<a href="${sourceUrl}" target="_blank" class="text-teal-500 underline hover:text-teal-700 dark:text-teal-400 dark:hover:text-teal-600">Source ${i + 1}</a>`
            )
            .join(', ');

        // Return the complete HTML for a single table row
        return `
            <tr>
              <!-- Mobile: Full-width container -->
              <td class="py-2 px-0 sm:px-8 text-lg w-full">
                <div class="border border-gray-300 rounded-lg py-6 px-8 mb-4 bg-white dark:border-gray-600 dark:bg-gray-700">

                  <!-- Mobile: Centered title -->
                  <h3 class="text-xl font-bold mb-2 text-gray-800 dark:text-gray-200 text-center sm:text-left">${item.news_number}: ${item.news_title}</h3>

                  <!-- STATIC PARAGRAPHS ARE INJECTED HERE -->
                  ${paragraphHtml}

                  ${sourcesHtml ? `<div class="mt-4 text-sm text-gray-600 dark:text-gray-300">${sourcesHtml}</div>` : ""}
                </div>
              </td>
            </tr>
        `;
    }).join('\n');
}


// 3. Get the current date for the header.
const currentDateIso = new Date().toISOString();

// 4. Assemble the complete HTML document, injecting the pre-rendered rows.
const completeHtml = `
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Daily News Digest</title>

    <!-- Tailwind CSS CDN -->
    <script src="https://cdn.tailwindcss.com"><\/script>
    <script>
      tailwind.config = { darkMode: "class", theme: { extend: {} } };
    <\/script>
  </head>
  <body class="bg-gray-50 font-sans dark:bg-gray-900 dark:text-gray-100">
    <div class="container mx-auto max-w-5xl p-8">
      <div class="flex justify-between items-center mb-8">
        <h1
          id="main-header-date"
          class="text-3xl font-extrabold text-center text-white bg-gradient-to-r from-teal-500 to-blue-500 py-4 rounded-xl flex-grow mr-4 dark:from-teal-700 dark:to-blue-700"
        >
          Daily News Digest
        </h1>
        <button
          id="darkModeToggle"
          class="p-3 rounded-full bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-gray-200 shadow-md hover:scale-105 transition-transform duration-200 ease-in-out"
          aria-label="Toggle dark mode"
        >
          <svg id="sunIcon" class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 3v1m0 16v1m9-9h1M3 12h1m15.325-6.675l-.707-.707M6.675 18.325l-.707-.707M18.325 18.325l-.707.707M5.675 6.675l-.707.707M12 18a6 6 0 110-12 6 6 0 010 12z"></path></svg>
          <svg id="moonIcon" class="w-6 h-6 hidden" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z"></path></svg>
        </button>
      </div>
      <div class="overflow-x-auto">
        <table class="min-w-full bg-white shadow-lg rounded-xl dark:bg-gray-800">
          <thead>
            <tr>
              <th class="sr-only">News Content</th>
            </tr>
          </thead>
          <tbody id="news-container" class="pb-6">
            <!-- STATIC CONTENT IS INJECTED HERE -->
            ${tableRowsHtml}
          </tbody>
        </table>
      </div>
      <p class="mt-6 text-gray-600 text-center text-lg dark:text-gray-400">
        END
      </p>
    </div>

    <!-- Client-side script for interactivity -->
    <script>
      const mainHeaderDate = document.getElementById("main-header-date");
      const darkModeToggle = document.getElementById("darkModeToggle");
      const sunIcon = document.getElementById("sunIcon");
      const moonIcon = document.getElementById("moonIcon");

      // --- Dark Mode Logic (Unchanged) ---
      function applyDarkMode(isDark) {
        const htmlElement = document.documentElement;
        if (isDark) {
          htmlElement.classList.add("dark");
          sunIcon.classList.add("hidden");
          moonIcon.classList.remove("hidden");
        } else {
          htmlElement.classList.remove("dark");
          sunIcon.classList.remove("hidden");
          moonIcon.classList.add("hidden");
        }
      }
      const savedTheme = localStorage.getItem("theme");
      applyDarkMode(savedTheme === "dark");
      darkModeToggle.addEventListener("click", () => {
        const isCurrentlyDark = document.documentElement.classList.contains("dark");
        applyDarkMode(!isCurrentlyDark);
        localStorage.setItem("theme", isCurrentlyDark ? "light" : "dark");
      });

      // --- Date Formatting for Header (Unchanged) ---
      function formatDateForHeader() {
        const dateString = "${currentDateIso}";
        try {
            const date = new Date(dateString);
            if (isNaN(date.getTime())) return "DATE (IST)";
            const options = { timeZone: "Asia/Kolkata", day: "numeric", month: "short", year: "numeric" };
            const formatter = new Intl.DateTimeFormat("en-GB", options);
            return \`\${formatter.format(date)} (IST)\`;
        } catch (e) {
            console.error("Date formatting error:", e);
            return "DATE (IST)";
        }
      }

      // --- Function to run on page load ---
      function initializePage() {
        mainHeaderDate.textContent = \`Daily News Digest - \${formatDateForHeader()}\`;
      }

      document.addEventListener("DOMContentLoaded", initializePage);
    <\/script>
  </body>
</html>
`;

// 5. Output the result as a Base64 string.
const htmlBase64 = Buffer.from(completeHtml, 'utf8').toString('base64');

return [
    { 
        json: { 
            htmlBase64: htmlBase64 
        } 
    }
];

// 1. Get the tweets data array from the "Aggregate Tweets" node.
const inputData = $('Aggregate Tweets').first().json.tweets;

// 2. Serialize the JavaScript array into a JSON string for injection.
const inputDataJson = JSON.stringify(inputData, null, 2);


// 3. Assemble the complete HTML document, injecting the serialized data.
const completeHtml = `
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Daily Posts Summary</title>

    <script>
      // *** THIS IS WHERE YOUR DATA IS INJECTED ***
      const inputData = ${inputDataJson};

      /**
       * Transforms the flat inputData structure into the original grouped postsData structure.
       * @param {Array} flatData - An array of post objects, each containing a 'user' field.
       * @returns {Array} An array of user objects, each containing a 'tweets' array.
       */
      function input_data_transformation(flatData) {
        const userMap = {};

        // 1. Group the flat posts by username
        flatData.forEach((post) => {
          const username = post.user;

          if (!userMap[username]) {
            userMap[username] = {
              user: username,
              tweets: [],
            };
          }

          const { user, ...tweetDetails } = post;
          userMap[username].tweets.push(tweetDetails);
        });

        // 2. Convert the object map into the final array structure
        const finalPostsData = Object.values(userMap);

        // Sort tweets within each user group by date ascending (Oldest first)
        finalPostsData.forEach(userObject => {
            userObject.tweets.sort((a, b) => {
                return new Date(a.createdAt) - new Date(b.createdAt);
            });
        });

        return finalPostsData;
      }

      // Convert the flat input data into the expected grouped postsData structure
      const postsData = input_data_transformation(inputData);
    <\/script>

    <!-- Initial script block for utility functions (unchanged) -->
    <script type="text/javascript">
      var gk_isXlsx = false;
      var gk_xlsxFileLookup = {};
      var gk_fileData = {};
      function filledCell(cell) {
        return cell !== "" && cell != null;
      }
      function loadFileData(filename) {
        if (gk_isXlsx && gk_xlsxFileLookup[filename]) {
          try {
            var workbook = XLSX.read(gk_fileData[filename], { type: "base64" });
            var firstSheetName = workbook.SheetNames[0];
            var worksheet = workbook.Sheets[firstSheetName];
            var jsonData = XLSX.utils.sheet_to_json(worksheet, {
              header: 1,
              blankrows: false,
              defval: "",
            });
            var filteredData = jsonData.filter((row) => row.some(filledCell));
            var headerRowIndex = filteredData.findIndex(
              (row, index) =>
                row.filter(filledCell).length >=
                filteredData[index + 1]?.filter(filledCell).length
            );
            if (headerRowIndex === -1 || headerRowIndex > 25) {
              headerRowIndex = 0;
            }
            var csv = XLSX.utils.aoa_to_sheet(
              filteredData.slice(headerRowIndex)
            );
            csv = XLSX.utils.sheet_to_csv(csv, { header: 1 });
            return csv;
          } catch (e) {
            console.error(e);
            return "";
          }
        }
        return gk_fileData[filename] || "";
      }
    <\/script>

    <!-- Tailwind CSS CDN -->
    <script src="https://cdn.tailwindcss.com"><\/script>
    <script>
      tailwind.config = { darkMode: "class", theme: { extend: {} } };
    <\/script>
  </head>
  <body class="bg-gray-50 font-sans dark:bg-gray-900 dark:text-gray-100">
    <div class="container mx-auto max-w-5xl p-8">
      <div class="flex justify-between items-center mb-8">
        <h1
          id="main-header-date"
          class="text-3xl font-extrabold text-center text-white bg-gradient-to-r from-teal-500 to-blue-500 py-4 rounded-xl flex-grow mr-4 dark:from-teal-700 dark:to-blue-700"
        >
          Daily Posts Summary
        </h1>
        <button
          id="darkModeToggle"
          class="p-3 rounded-full bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-gray-200 shadow-md hover:scale-105 transition-transform duration-200 ease-in-out"
          aria-label="Toggle dark mode"
        >
          <svg id="sunIcon" class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 3v1m0 16v1m9-9h1M3 12h1m15.325-6.675l-.707-.707M6.675 18.325l-.707-.707M18.325 18.325l-.707.707M5.675 6.675l-.707.707M12 18a6 6 0 110-12 6 6 0 010 12z"></path></svg>
          <svg id="moonIcon" class="w-6 h-6 hidden" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z"></path></svg>
        </button>
      </div>
      <div class="overflow-x-auto">
        <table
          class="min-w-full bg-white shadow-lg rounded-xl dark:bg-gray-800"
        >
          <thead>
            <tr>
              <th class="sr-only">Post Content</th>
            </tr>
          </thead>
          <tbody id="posts-container" class="pb-6"></tbody>
        </table>
      </div>
      <p class="mt-6 text-gray-600 text-center text-lg dark:text-gray-400">
        END
      </p>
    </div>

    <!-- Main application script -->
    <script>
      const postsContainer = document.getElementById("posts-container");
      const mainHeaderDate = document.getElementById("main-header-date");
      const darkModeToggle = document.getElementById("darkModeToggle");
      const sunIcon = document.getElementById("sunIcon");
      const moonIcon = document.getElementById("moonIcon");

      function applyDarkMode(isDark) {
        const htmlElement = document.documentElement;
        if (isDark) {
          htmlElement.classList.add("dark");
          sunIcon.classList.add("hidden");
          moonIcon.classList.remove("hidden");
        } else {
          htmlElement.classList.remove("dark");
          sunIcon.classList.remove("hidden");
          moonIcon.classList.add("hidden");
        }
      }
      const savedTheme = localStorage.getItem("theme");
      applyDarkMode(savedTheme === "dark");
      darkModeToggle.addEventListener("click", () => {
        const isCurrentlyDark =
          document.documentElement.classList.contains("dark");
        applyDarkMode(!isCurrentlyDark);
        localStorage.setItem("theme", isCurrentlyDark ? "light" : "dark");
      });

      function formatDateForHeader(dateString) {
        try {
          const d = new Date(dateString); 
          return isNaN(d.getTime())
            ? "Date N/A"
            : d.toLocaleDateString("en-US", {
                month: "short",
                day: "numeric",
                year: "numeric",
              });
        } catch (e) {
          return "Date N/A";
        }
      }
      function formatTimestamp(dateString) {
        try {
          const d = new Date(dateString);
          return isNaN(d.getTime())
            ? dateString
            : new Intl.DateTimeFormat("en-US", {
                month: "short",
                day: "numeric",
                hour: "numeric",
                minute: "2-digit",
                hour12: true,
              }).format(d);
        } catch (e) {
          console.error("Date format error:", e);
          return dateString;
        }
      }

      function renderPosts() {
        let latestDate = new Date(0); 
        postsData.forEach(userObject => {
            userObject.tweets.forEach(post => {
                const tweetDate = new Date(post.createdAt);
                if (tweetDate > latestDate) {
                    latestDate = tweetDate; 
                }
            });
        });

        if (latestDate.getTime() > 0) {
          const headerDate = formatDateForHeader(latestDate);
          mainHeaderDate.textContent = \`Daily Posts - \${headerDate} (IST)\`;
        } else {
          mainHeaderDate.textContent = \`Daily Posts (No Data)\`;
        }

        postsContainer.innerHTML = "";
        let tweetIdCounter = 0;

        postsData.forEach((userObject, userIndex) => {
          // *** CHANGE 3: Center user header on mobile, left-align on larger screens ***
          const userHeaderHtml = \`<tr><td class="pt-8 pb-4 px-8 text-center sm:text-left"><h2 class="text-2xl font-bold text-gray-800 dark:text-gray-200">Posts by @\${userObject.user}</h2></td></tr>\`;
          postsContainer.insertAdjacentHTML("beforeend", userHeaderHtml);

          userObject.tweets.forEach((post) => {
            tweetIdCounter++;
            const formattedTimestamp = formatTimestamp(post.createdAt);

            const cleanTweetText = (post.tweetText || "")
              .replace(/\\\\n/g, "\\n")
              .replace(/\\\\/g, "\\n")
              .replace(/\\n{3,}/g, "\\n\\n");

            const cleanTweetSummary = (post.tweetSummary || "")
              .replace(/\\\\n/g, "\\n")
              .replace(/\\\\/g, "\\n")
              .replace(/\\n{3,}/g, "\\n\\n");

            const postHtml = \`
              <tr>
                <!-- *** CHANGE 4: Remove horizontal padding on mobile, add it back on larger screens *** -->
                <td class="py-2 px-0 sm:px-8 text-lg w-full">
                  <div id="cell-\${tweetIdCounter}" class="border border-gray-300 rounded-lg py-6 px-8 mb-2 bg-white dark:border-gray-600 dark:bg-gray-700">
                    <span class="tweet-text whitespace-pre-wrap dark:text-gray-200" id="tweet-\${tweetIdCounter}">\${
              cleanTweetText.length > 280
                ? cleanTweetText.substring(0, 280) + "..."
                : cleanTweetText
            }</span>
                    <span class="tweet-full whitespace-pre-wrap dark:text-gray-200" id="tweet-full-\${tweetIdCounter}" style="display: none;">\${cleanTweetText}</span>
                    \${
                      cleanTweetText.length > 280
                        ? \`<br><a href="#" class="text-sm text-purple-500 underline hover:text-purple-700 toggle-text dark:text-purple-400 dark:hover:text-purple-600" data-tweet-id="\${tweetIdCounter}">Show More</a>\`
                        : ""
                    }
                    <div class="flex justify-between mt-4">
                      <details id="details-\${tweetIdCounter}"><summary class="text-sm text-teal-500 underline cursor-pointer hover:text-teal-700 pl-2 dark:text-teal-400 dark:hover:text-teal-600">Summarize Tweet</summary></details>
                      \${
                        post.twitterUrl
                          ? \`<a href="\${post.twitterUrl}" target="_blank" class="text-sm text-teal-500 underline hover:text-teal-700 pr-2 dark:text-teal-400 dark:hover:text-teal-600">Link to Original Tweet</a>\`
                          : ""
                      }
                    </div>
                    <div class="mt-2">
                      <p id="summary-\${tweetIdCounter}" class="text-base text-gray-600 dark:text-gray-300" style="display: none;">\${cleanTweetSummary}</p>
                    </div>
                    <div class="mt-4 text-sm text-gray-500 pl-2 dark:text-gray-400">\${formattedTimestamp}</div>
                  </div>
                </td>
              </tr>
            \`;
            postsContainer.insertAdjacentHTML("beforeend", postHtml);
          });

          if (userIndex < postsData.length - 1) {
            const separatorHtml = \`<tr><td class="py-6 px-8"><hr class="border-gray-300 dark:border-gray-600" /></td></tr>\`;
            postsContainer.insertAdjacentHTML("beforeend", separatorHtml);
          }
        });

        document.querySelectorAll("details").forEach((details) => {
          details.removeEventListener("toggle", handleDetailsToggle);
          details.addEventListener("toggle", handleDetailsToggle);
        });
      }

      document.addEventListener("click", (e) => {
        if (e.target.classList.contains("toggle-text")) {
          e.preventDefault();
          const button = e.target;
          const tweetId = button.getAttribute("data-tweet-id");
          const shortText = document.getElementById(\`tweet-\${tweetId}\`);
          const fullText = document.getElementById(\`tweet-full-\${tweetId}\`);
          if (shortText.style.display === "none") {
            shortText.style.display = "inline";
            fullText.style.display = "none";
            button.textContent = "Show More";
          } else {
            shortText.style.display = "none";
            fullText.style.display = "inline";
            button.textContent = "Show Less";
          }
        }
      });

      function handleDetailsToggle(e) {
        const details = e.target;
        const tweetId = details.id.split("-")[1];
        const cellContainer = document.getElementById(\`cell-\${tweetId}\`);
        const summaryElement = document.getElementById(\`summary-\${tweetId}\`);
        if (details.open) {
          cellContainer.classList.add("bg-teal-50", "dark:bg-gray-600");
          summaryElement.style.display = "block";
        } else {
          cellContainer.classList.remove("bg-teal-50", "dark:bg-gray-600");
          summaryElement.style.display = "none";
        }
      }

      document.addEventListener("DOMContentLoaded", renderPosts);
    <\/script>
  </body>
</html>
`;

// 4. Output the result as a Base64 string.
const htmlBase64 = Buffer.from(completeHtml, 'utf8').toString('base64');

return [
    { 
        json: { 
            htmlBase64: htmlBase64 
        } 
    }
];

// 1. Get the video summary data array from the "Aggregate Summary" node.
const videos = $('Aggregate Summary').first().json.summary_data;

/**
 * Converts a summary string with custom markdown into a formatted HTML string.
 * This function runs on the server-side within the n8n node.
 * @param {string} summaryText - The raw summary text from your input.
 * @returns {string} - The formatted HTML string.
 */
function renderMarkdown(summaryText) {
    if (!summaryText) return '';

    const lines = summaryText.split('\n');
    let html = '';

    // Tailwind class for consistent paragraph spacing (increased from mb-2 to mb-3)
    const paragraphSpacingClass = "mb-3"; 

    for (const line of lines) {
        let processedLine = line.trim();

        // Create a paragraph break for empty lines (changed h-4 to h-2 for a smaller break)
        if (processedLine === '') {
            html += '<div class="h-2"></div>'; // h-2 provides a small vertical space
            continue;
        }

        // Handle bolding (**text**) within the line first
        processedLine = processedLine.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

        // Handle headings (#, ##, ###) - Now text-lg (18px) which is 1px larger than text-base (16px)
        if (processedLine.startsWith('### ')) {
            html += `<p class="text-lg font-bold underline ${paragraphSpacingClass}">${processedLine.substring(4)}</p>`;
        } else if (processedLine.startsWith('## ')) {
            html += `<p class="text-lg font-bold underline ${paragraphSpacingClass}">${processedLine.substring(3)}</p>`;
        } else if (processedLine.startsWith('# ')) {
            html += `<p class="text-lg font-bold underline ${paragraphSpacingClass}">${processedLine.substring(2)}</p>`;
        }
        // Handle bullet points (* or -) as regular paragraphs (no custom bullet, no special alignment)
        else if (processedLine.startsWith('* ')) {
            html += `<p class="${paragraphSpacingClass}">${processedLine.substring(2)}</p>`;
        } else if (processedLine.startsWith('- ')) {
            html += `<p class="${paragraphSpacingClass}">${processedLine.substring(2)}</p>`;
        }
        // Handle regular paragraphs
        else {
            html += `<p class="${paragraphSpacingClass}">${processedLine}</p>`;
        }
    }
    return html;
}

// 2. SERVER-SIDE RENDERING: Generate the static HTML for the table rows.
let tableRowsHtml = '';

if (!videos || videos.length === 0) {
    tableRowsHtml = '<tr><td class="pt-8 pb-4 px-8 text-center text-gray-500 dark:text-gray-400">No video summaries to display.</td></tr>';
} else {
    tableRowsHtml = videos.map(video => {
        const summaryHtml = renderMarkdown(video.summary);

        return `
            <tr>
              <!-- Mobile Responsiveness: No horizontal padding on mobile, added back on larger screens -->
              <td class="py-2 px-0 sm:px-8 text-lg w-full">
                <div class="border border-gray-300 rounded-lg py-6 px-8 mb-4 bg-white dark:border-gray-600 dark:bg-gray-700">

                  <h3 class="text-xl font-bold mb-1 text-gray-800 dark:text-gray-200">${video.video_title}</h3>

                  <!-- URL: underline added here -->
                  <p class="text-sm text-gray-500 underline dark:text-gray-400 mb-4">${video.url}</p>

                  <div class="text-base dark:text-gray-300">
                    ${summaryHtml}
                  </div>

                </div>
              </td>
            </tr>
        `;
    }).join('\n');
}


// 3. Get the current date for the header.
const currentDateIso = new Date().toISOString();

// 4. Assemble the complete HTML document, injecting the pre-rendered rows.
const completeHtml = `
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Video Summary Digest</title>

    <!-- Tailwind CSS CDN -->
    <script src="https://cdn.tailwindcss.com"><\/script>
    <script>
      tailwind.config = { darkMode: "class", theme: { extend: {} } };
    <\/script>
  </head>
  <body class="bg-gray-50 font-sans dark:bg-gray-900 dark:text-gray-100">
    <div class="container mx-auto max-w-5xl p-8">
      <div class="flex justify-between items-center mb-8">
        <h1
          id="main-header-date"
          class="text-3xl font-extrabold text-center text-white bg-gradient-to-r from-teal-500 to-blue-500 py-4 rounded-xl flex-grow mr-4 dark:from-teal-700 dark:to-blue-700"
        >
          Video Summary Digest
        </h1>
        <button
          id="darkModeToggle"
          class="p-3 rounded-full bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-gray-200 shadow-md hover:scale-105 transition-transform duration-200 ease-in-out"
          aria-label="Toggle dark mode"
        >
          <svg id="sunIcon" class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 3v1m0 16v1m9-9h1M3 12h1m15.325-6.675l-.707-.707M6.675 18.325l-.707-.707M18.325 18.325l-.707.707M5.675 6.675l-.707.707M12 18a6 6 0 110-12 6 6 0 010 12z"></path></svg>
          <svg id="moonIcon" class="w-6 h-6 hidden" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z"></path></svg>
        </button>
      </div>
      <div class="overflow-x-auto">
        <table class="min-w-full bg-white shadow-lg rounded-xl dark:bg-gray-800">
          <thead>
            <tr>
              <th class="sr-only">Video Content</th>
            </tr>
          </thead>
          <tbody id="videos-container" class="pb-6">
            <!-- STATIC CONTENT IS INJECTED HERE -->
            ${tableRowsHtml}
          </tbody>
        </table>
      </div>
      <p class="mt-6 text-gray-600 text-center text-lg dark:text-gray-400">
        END
      </p>
    </div>

    <!-- Client-side script for interactivity -->
    <script>
      const mainHeaderDate = document.getElementById("main-header-date");
      const darkModeToggle = document.getElementById("darkModeToggle");
      const sunIcon = document.getElementById("sunIcon");
      const moonIcon = document.getElementById("moonIcon");

      function applyDarkMode(isDark) {
        const htmlElement = document.documentElement;
        if (isDark) {
          htmlElement.classList.add("dark");
          sunIcon.classList.add("hidden");
          moonIcon.classList.remove("hidden");
        } else {
          htmlElement.classList.remove("dark");
          sunIcon.classList.remove("hidden");
          moonIcon.classList.add("hidden");
        }
      }
      const savedTheme = localStorage.getItem("theme");
      applyDarkMode(savedTheme === "dark");
      darkModeToggle.addEventListener("click", () => {
        const isCurrentlyDark = document.documentElement.classList.contains("dark");
        applyDarkMode(!isCurrentlyDark);
        localStorage.setItem("theme", isCurrentlyDark ? "light" : "dark");
      });

      function formatDateForHeader() {
        const dateString = "${currentDateIso}";
        try {
            const date = new Date(dateString);
            if (isNaN(date.getTime())) return "DATE (IST)";
            const options = { timeZone: "Asia/Kolkata", day: "numeric", month: "short", year: "numeric" };
            const formatter = new Intl.DateTimeFormat("en-GB", options);
            return \`\${formatter.format(date)} (IST)\`;
        } catch (e) {
            console.error("Date formatting error:", e);
            return "DATE (IST)";
        }
      }

      function initializePage() {
        mainHeaderDate.textContent = \`Video Summary Digest - \${formatDateForHeader()}\`;
      }

      document.addEventListener("DOMContentLoaded", initializePage);
    <\/script>
  </body>
</html>
`;

// 5. Output the result as a Base64 string in the required JSON format.
const htmlBase64 = Buffer.from(completeHtml, 'utf8').toString('base64');

return [
    { 
        json: { 
            htmlBase64: htmlBase64 
        } 
    }
];
